var bl =require('bl'),
    url = process.argv[2],
    http = require('http');
http.get(url, function (response) {
    response.setEncoding('utf8');
    response.pipe(bl(function (err, data) {
        if (err) return console.log(err);
        data = data.toString();
        console.log(data.length);
        console.log(data);
    }));
});