var http =require('http'),
    fs = require('fs'),
    port = Number(process.argv[2]),
    fileName = process.argv[3],
    server;
server = http.createServer(function (req, res) {
    res.writeHead(200, 'content-type:text/plain');
    fs.createReadStream(fileName).pipe(res);
});
server.listen(port);
