var url = process.argv[2],
    http = require('http');
http.get(url, function (response) {
    response.setEncoding('utf8');
    response.on('data', function (data) {
        console.log(data);
    });
    response.on('error', function (err) {
        // console.log(err);
    });
    response.on('end', function (info) {
        // console.log(info);
    });
});