var fs = require('fs'),
    dir = process.argv[2],
    filter = process.argv[3];
    // console.log(dir);
fs.readdir(dir, function (err, list) {
    // console.log(list.length);
    list.forEach(function (item) {
        if (item.substr(-(filter.length + 1)) === ('.' + filter)) {
            console.log(item);
        }
    });
});