var net = require('net'),
    port = process.argv[2],
    server;
server = net.createServer(function (socket) {
    var date = new Date();
    var year = date.getFullYear(),
        month = date.getMonth(),
        day = date.getDate(),
        hours = date.getHours(),
        minutes = date.getMinutes();
    month = month + 1;
    if (month < 10) {
        month = '0' + month;
    }
    if (hours < 10) {
        hours = '0' + hours;
    }
    socket.end(year + "-" + month + '-' + day + ' ' + hours + ':' + minutes);
});
server.listen(port);